import React,{Component} from 'react';
// import logo from './logo.svg';
import { Navbar,NavbarBrand } from 'reactstrap';
import Menu from './MenuComponent';
import DISHES from '../shared/dishes';
import '../shared/App.css';
import DishdetailComponent from './components/DishdetailComponent';


class Main extends Component{
  constructor(props){
    super(props);
    this.state={
      dishes:DISHES,
      selectedDish:null
    };
  }
  onDishSelect(dishId){
    this.setState({selectedDish:dishId});
  }
  render(){
    return(
      <div className="App">
      <Navbar dark color="primary">
        <div className="container">
          <NavbarBrand href="/">Restaurant</NavbarBrand>
        </div>
      </Navbar>
      <Menu dishes={this.state.dishes}
      onClick={(dishId) => this.onDishSelect(dishId)} />
      <DishdetailComponent dish={this.state.dishes.filter((dish)=>dish.id===this.state.selectedDish)[0]}/>//filterfunction supplies it as an array with only one element
    </div>
      );
  }
}

export default Main;
