import React , {Component} from 'react';
import {Media,Card,CardImg,CardImgOverlay,CardText,CardBody,CardTitle} from 'reactstrap';

class DishdetailComponent extends Component{

	constructor(props){
		super(props);
		this.state={
		}
	 
	}
	renderDish(dish){
		//put the below code in render
		// if(dish!=null){
		// 	// console.log("if is invoked");
		// 	const menu=this.props.dishes.map((dish)=>{//it is a for loop
		// 	return (<div key={dish.id} className="col-12 col-md-5 m-1">
		// 			<Card onClick={() => this.onDishSelect(dish)}>
		// 				<CardImg width="100%" src={dish.image} alt={dish.name}/>	
		// 				<CardImgOverlay>
		// 					<CardTitle>{dish.name}</CardTitle>
		// 				</CardImgOverlay>
		// 			</Card> 
		// 		</div>
				
		// 		);
			return(
				 <div className="col-12 col-md-5 m-1">
				<Card>
					<CardImg width="100%" src={dish.image} alt={dish.name} className/>
					<CardBody>
						<CardTitle>{dish.name}</CardTitle>
						<CardText>{dish.description}</CardText>
					</CardBody>	
				</Card>
				 </div>

				
				);
	}
	renderComments(dish){
		if(dish!=null){
			return(
				 <div className="col-12 col-md-5 m-1">
						<h4>Comments</h4>
				 </div>
				);
		}
		else{
			return(
				<div></div>
				
				);
		}
	}
	
	render(){
		
		return(
			<div className="row">
				{this.renderDish(this.props.dish)}
				{this.renderComments(this.props.dish)}
			</div>
			);
	}
}

export default DishdetailComponent;
